# Function to extract universities that are in the JSON file but not in the university_country_map
def extract_universities_not_in_map(json_file_path):
    """
    Extract university names from the JSON file that are not found in the university_country_map.
    
    Args:
        json_file_path (str): Path to the input JSON file.
        university_country_map (dict): A dictionary with university names as keys and their respective countries as values.
    
    Returns:
        list: A list of university names not found in the university_country_map.
    """
    try:
        with open(json_file_path, 'r') as file:
            data = json.load(file)
        
        # List to hold universities not found in the map
        universities_not_in_map = []
        
        # Iterate through the JSON data and check the affiliations
        for entry in data:
            if "aff" in entry and entry["aff"]:
                affiliations = [aff.strip() for aff in entry["aff"].split(";") if aff.strip()]
                for aff in affiliations:
                    if is_university(aff):
                        # Check if the affiliation is a university, and not already in the map
                        for university_name in university_country_map.keys():
                            if university_name.lower() in aff.lower():  # Case insensitive match
                                break
                        else:
                            # If no match was found in the map, add the university name
                            universities_not_in_map.append(aff)

        # Remove duplicates by converting the list to a set and back to a list
        return list(set(universities_not_in_map))

    except FileNotFoundError:
        print(f"File not found: {json_file_path}")
        return []
    except json.JSONDecodeError:
        print(f"Invalid JSON in file: {json_file_path}")
        return []
    except Exception as e:
        print(f"An error occurred: {e}")
        return []

def remove_company_affiliations(json_file_path, company_map):
    """
    Reads affiliations from a JSON file and removes company affiliations listed in the company_country_map.

    :param json_file_path: Path to the JSON file containing affiliations.
    :param company_map: A dictionary with company names as keys.
    :return: List of affiliations excluding company names.
    """
    try:
        # Extract affiliations
        with open(json_file_path, 'r') as file:
            data = json.load(file)
        
        # Assuming extract_affiliations returns a list of affiliations
        affiliations = extract_affiliations(json_file_path)
        
        # Convert company names to lowercase for case-insensitive comparison
        company_names_lower = {company.lower() for company in company_map.keys()}
        
        # Filter out company names
        filtered_affiliations = [
            affiliation for affiliation in affiliations
            if not any(company_name in affiliation.lower() for company_name in company_names_lower)
        ]
        
        return filtered_affiliations

    except FileNotFoundError:
        print(f"File not found: {json_file_path}")
    except json.JSONDecodeError:
        print(f"Invalid JSON in file: {json_file_path}")
    except Exception as e:
        print(f"An error occurred: {e}")
        
# Function to determine company country based on keywords in the affiliation
def get_company_country_from_keywords(affiliation):
    """
    Get the country for a given affiliation string based on company-related keywords.

    Args:
        affiliation (str): The affiliation string to check.

    Returns:
        str: The country of the company, or 'Unknown' if no match is found.
    """
    # Check if any known company keywords appear in the affiliation
    affiliation = affiliation.lower()  # Make it case-insensitive
    for company_name in company_country_map.keys():
        if company_name.lower() in affiliation:
            return company_country_map[company_name]  # Return the country if a match is found
    return "Unknown"  # Return "Unknown" if no match is found

# check the affiliations
def extract_affiliations(json_file_path):
    """
    Extract affiliations from a JSON dataset, filter by university keywords,
    and return unique non-university affiliations.

    Args:
        json_file_path (str): Path to the input JSON file.

    Returns:
        list: Unique non-university affiliations.
    """
    university_keywords = get_university_keywords()
    def is_university(affiliation):
        """Check if an affiliation contains university-related keywords."""
        return any(keyword.lower() in affiliation.lower() for keyword in university_keywords)

    # Load the JSON data
    with open(json_file_path, "r") as json_file:
        data = json.load(json_file)

    # Extract all affiliations
    all_affiliations = set()  # Use a set to store unique affiliations
    for entry in data:
        if "aff" in entry and entry["aff"]:
            affiliations = [aff.strip() for aff in entry["aff"].split(";") if aff.strip()]
            all_affiliations.update(affiliations)

    # Filter affiliations
    non_university_affiliations = [aff for aff in all_affiliations if not is_university(aff)]

    return sorted(non_university_affiliations)  # Return sorted unique non-university affiliations

